import { Injectable } from '@angular/core';
import { Recipe } from './recipe.modal';

@Injectable({
  providedIn: 'root',
})
export class RecipesService {
  private recipes: Recipe[] = [
    {
      id: 'r1',
      title: 'Pizza',
      imageUrl: 'data:image/jpeg;base64,',
      ingredints: ['Cheese', 'onion', 'tomatoes'],
    },
    {
      id: 'r2',
      title: 'Sandwhich',
      imageUrl: 'data:image/jpeg;base64,',
      ingredints: ['bread', 'tomatoes', 'mayonnaise'],
    },
    {
      id: 'r3',
      title: 'Cake',
      imageUrl: 'data:image/jpeg;base64,',
      ingredints: ['Chocolate', 'Milk', 'flor'],
    },
  ];

  constructor() {}

  getAllRecipes() {
    return [...this.recipes];
  }

  geteRecipe(recipeId: string) {
    return {
      ...this.recipes.find(recipe => {
        return recipe.id === recipeId;
      }),
    };
  }
}
